import React, { useState, useCallback, useEffect } from 'react';
import { View, Text, Modal, TouchableOpacity, StyleSheet, FlatList, Dimensions, Image, TextInput } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import logoImage from '../../assets/logo.png';
import bache from '../../assets/bache.jpeg';
import fofo from '../../assets/fofo.jpeg';
import reparacion from '../../assets/reparacion.jpeg';
import { launchImageLibrary } from 'react-native-image-picker';

const screenWidth = Dimensions.get('window').width;
const screenHeight = Dimensions.get('window').height;

const Students = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const menuOptions = ["Denuncias", "Reporte", "Usuario"];
  const navigation = useNavigation();
  const [titulo, setTitulo] = useState('');
  const [imageUri, setImageUri] = useState(null);
  useFocusEffect(
    useCallback(() => {
      const unsubscribe = () => setIsMenuVisible(false);
      return unsubscribe;
    }, [])
  );

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  const selectOption = (option) => {
    console.log(`Opción seleccionada: ${option}`);
    setIsMenuVisible(false);
  };
  // Datos de prueba para las publicaciones
const publicacionesData = [
    {
      id: 1,
      titulo: "HAY UN BACHE EN BERNARDO QUINTANA CON CONSTITUYENTES",
      imagen: require('../../assets/bache.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
    {
      id: 2,
      titulo: "Publicación 2",
      imagen: require('../../assets/reparacion.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
    {
      id: 3,
      titulo: "QUE GUAPO MI AMOR",
      imagen: require('../../assets/fofo.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
  ];


  const [publicaciones, setPublicaciones] = useState(publicacionesData);

  const handleSeleccionarImagen = () => {
  // Configuración para la selección de imágenes
  const options = {
    mediaType: 'photo', // Solo permitir seleccionar fotos
    quality: 0.5, // Calidad de la imagen (0.0 - 1.0)
  };

  // Abrir el cuadro de diálogo para seleccionar una imagen
  launchImageLibrary(options, (response) => {
    if (response.didCancel) {
      // El usuario canceló la selección de imagen
    } else if (response.errorMessage) {
      // Ocurrió un error al seleccionar la imagen
      console.error('Error al seleccionar la imagen: ', response.errorMessage);
    } else {
      // Imagen seleccionada con éxito
      const { uri } = response;

      // Actualizar el estado 'imageUri' con la ruta de la imagen seleccionada
      setImageUri(uri);
    }
  });
};

const handleEnviarPublicacion = () => {
  // Validar que se haya seleccionado una imagen y que se haya ingresado un título
  if (!imageUri) {
    alert('Por favor, selecciona una imagen.');
    return;
  }

  if (!titulo) {
    alert('Por favor, ingresa un título.');
    return;
  }

  // Aquí puedes implementar la lógica para enviar la publicación al servidor o almacenarla en una base de datos
  // Puedes usar 'titulo' para obtener el título de la publicación y 'imageUri' para obtener la ruta de la imagen
  // Por ejemplo, puedes utilizar una solicitud HTTP para enviar los datos al servidor.

  // Luego de enviar la publicación con éxito, puedes restablecer los estados de 'titulo' e 'imageUri' si es necesario:
  setTitulo('');
  setImageUri(null);

  // Mostrar un mensaje de éxito o redirigir al usuario a la página de inicio, etc.
  alert('Publicación enviada con éxito.');
};

  useFocusEffect(
    useCallback(() => {
      const unsubscribe = () => setIsMenuVisible(false);
      return unsubscribe;
    }, [])
  );


  return (
    <View style={styles.container}>
      <Image
        source={logoImage}
        style={[styles.logo, { width: screenWidth * 0.8, height: screenHeight * 0.2 }]}
        resizeMode="contain"
      />
      <TouchableOpacity onPress={toggleMenu} style={styles.menuTrigger}>
        <Text style={styles.menuTriggerText}>Menú☰</Text>
      </TouchableOpacity>
      <TextInput
        style={styles.input}
        onChangeText={(text) => setTitulo(text)}
        value={titulo}
        placeholder="Ingresa el título de la publicación"
      />
      <TouchableOpacity
        style={styles.subirImagenButton}
        onPress={handleSeleccionarImagen}
      >
        <Text style={styles.subirImagenButtonText}>Seleccionar Imagen</Text>
      </TouchableOpacity>

       <View style={styles.container}>
      <FlatList
        data={publicaciones}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View>
            <Text>{item.titulo}</Text>
            <Image source={item.imagen} style={{ width: 500, height: 500 }} />
          </View>
        )}
      />
    </View>

    <Modal
        animationType="slide" // Cambiamos la animación para que el menú se deslice desde la izquierda
        transparent={true}
        visible={isMenuVisible}
        onRequestClose={() => {
          setIsMenuVisible(false);
        }}
      >
        <View style={styles.sidebar}>
        <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate('Denuncias')}
      >
        <FontAwesome name="exclamation-triangle" size={30} color="white" />
        <Text style={styles.buttonText}>Denuncias</Text>
      </TouchableOpacity>

      <TouchableOpacity 
       style={styles.button}
        onPress={() => navigation.navigate('Reporte')}>
        <FontAwesome name="bar-chart" size={30} color="white" />
        <Text style={styles.buttonText}>Reporte</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate('Login')}
      >
        <FontAwesome name="sign-out" size={30} color="white" />
        <Text style={styles.buttonText}>Cerrar Sesión</Text>
      </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#fff',
    borderRadius:10,
    borderwidth: 1,
  },
  menuTrigger: {
    position: 'absolute', 
    top: 0, 
    left: 0, 
    padding: 10,
  },
  menuTriggerText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  sidebar: {
    flex: 1,
    backgroundColor: '#dcffff',
  },
  menuItem: {
    fontSize: 18,
    padding: 10,
  },
  logo: {
    width: screenWidth,
    height: screenWidth / 4,
    resizeMode: 'contain',
    alignSelf: 'center',
  },
  button: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#0073e6',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  reportButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#34A853',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
    marginLeft: 10,
  },
});

export default Students;
