import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Button } from 'react-native';
import SQLite from 'react-native-sqlite-storage';


const DenunciaDetailScreen = ({ route }) => {
  const { tipo, descripcion } = route.params;
  const [inputText, setInputText] = useState('');
  const [imageUri, setImageUri] = useState(null);
  const db = SQLite.openDatabase({ name: 'mydatabase.db', location: 'default' });

// Asegúrate de que la base de datos se ha creado con éxito
db.transaction((tx) => {
  tx.executeSql(
    'CREATE TABLE IF NOT EXISTS textos (id INTEGER PRIMARY KEY AUTOINCREMENT, texto TEXT)',
    [],
    () => {
      console.log('Tabla creada con éxito');
    },
    (error) => {
      console.error('Error al crear la tabla: ', error);
    }
  );
});

const handleEnviarTexto = () => {
  db.transaction((tx) => {
    tx.executeSql(
      'INSERT INTO textos (texto) VALUES (?)',
      [inputText],
      (_, results) => {
        console.log('Texto almacenado con éxito');
      },
      (error) => {
        console.error('Error al almacenar el texto: ', error);
      }
    );
  });
};

  const handleSubirImagen = () => {
    // Aquí puedes implementar la lógica para subir una imagen
    // Puedes usar bibliotecas como react-native-image-picker o react-native-document-picker para seleccionar imágenes desde la galería o la cámara.
    alert('Subir imagen');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{tipo}</Text>
      <Text style={styles.descripcion}>{descripcion}</Text>

      <TextInput
        style={styles.input}
        onChangeText={(text) => setInputText(text)}
        value={inputText}
        placeholder="Escribe aquí"
      />

      <Button title="Enviar Texto" onPress={handleEnviarTexto} />

      <TouchableOpacity style={styles.subirImagenButton} onPress={handleSubirImagen}>
        <Text style={styles.subirImagenButtonText}>Subir Imagen</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  descripcion: {
    fontSize: 18,
    marginBottom: 20,
  },
  input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    marginTop: 10,
    padding: 5,
  },
  subirImagenButton: {
    marginTop: 20,
    backgroundColor: '#004AAD',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 10,
  },
  subirImagenButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default DenunciaDetailScreen;
