import React, { useState, useCallback } from 'react';
import { View, Text, Modal, TouchableOpacity, StyleSheet, FlatList, Dimensions, Image, TextInput } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import logoImage from '../../assets/logo.png';
import { launchImageLibrary } from 'react-native-image-picker';

const screenWidth = Dimensions.get('window').width;
const screenHeight = Dimensions.get('window').height;

const Students = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const navigation = useNavigation();
  const [titulo, setTitulo] = useState('');
  const [imageUri, setImageUri] = useState(null);

  useFocusEffect(
    useCallback(() => {
      const unsubscribe = () => setIsMenuVisible(false);
      return unsubscribe;
    }, [])
  );

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  const selectOption = (option) => {
    console.log(`Opción seleccionada: ${option}`);
    setIsMenuVisible(false);
  };

  const handleSeleccionarImagen = () => {
    // Configuración para la selección de imágenes
    const options = {
      mediaType: 'photo', // Solo permitir seleccionar fotos
      quality: 0.5, // Calidad de la imagen (0.0 - 1.0)
    };

    // Abrir el cuadro de diálogo para seleccionar una imagen
    launchImageLibrary(options, (response) => {
      if (response.didCancel) {
        // El usuario canceló la selección de imagen
      } else if (response.errorMessage) {
        // Ocurrió un error al seleccionar la imagen
        console.error('Error al seleccionar la imagen: ', response.errorMessage);
      } else {
        // Imagen seleccionada con éxito
        const { uri } = response;

        // Actualizar el estado 'imageUri' con la ruta de la imagen seleccionada
        setImageUri(uri);
      }
    });
  };

  const handleEnviarPublicacion = () => {
    // Código para enviar la publicación
    // ...
  };

  // Datos de prueba para las publicaciones
  const publicacionesData = [
    {
      id: 1,
      titulo: "HAY UN BACHE EN BERNARDO QUINTANA CON CONSTITUYENTES",
      imagen: require('../../assets/bache.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
    {
      id: 2,
      titulo: "Publicación 2",
      imagen: require('../../assets/reparacion.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
    {
      id: 3,
      titulo: "QUE GUAPO MI AMOR",
      imagen: require('../../assets/fofo.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
  ];
  const [publicaciones, setPublicaciones] = useState(publicacionesData);

  return (
    <View style={styles.container}>
      {/* Menu Button */}
      <TouchableOpacity onPress={toggleMenu} style={styles.menuButton}>
        <Text style={styles.menuButtonText}>Menu ☰</Text>
      </TouchableOpacity>
      {/* Main Content */}
      <Image
        source={logoImage}
        style={styles.logo}
        resizeMode="contain"
      />
      <View style={styles.postInputContainer}>
        <TextInput
          style={styles.input}
          onChangeText={setTitulo}
          value={titulo}
          placeholder="Ingresa el título de la publicación"
        />
        <TouchableOpacity
          style={styles.subirImagenButton}
          onPress={handleSeleccionarImagen}
        >
          <Text style={styles.subirImagenButtonText}>Seleccionar Imagen</Text>
        </TouchableOpacity>
      </View>

      <FlatList
        data={publicaciones}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.postCard}>
            <Text style={styles.postTitle}>{item.titulo}</Text>
            <Image source={item.imagen} style={styles.postImage} />
          </View>
        )}
        style={styles.flatList}
      />

      {/* Sidebar Modal */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={isMenuVisible}
        onRequestClose={() => setIsMenuVisible(false)}
      >

        <TouchableOpacity
          style={styles.modalOverlay}
          activeOpacity={1}
          onPressOut={() => setIsMenuVisible(false)}
        ></TouchableOpacity>
        <View style={styles.modalContent}>
          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('Denuncias')}
          >
            <FontAwesome name="exclamation-triangle" size={30} color="white" />
            <Text style={styles.buttonText}>Denuncias</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('Reporte')}
          >
            <FontAwesome name="bar-chart" size={30} color="white" />
            <Text style={styles.buttonText}>Reporte</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('Login')}
          >
            <FontAwesome name="sign-out" size={30} color="white" />
            <Text style={styles.buttonText}>Cerrar Sesión</Text>
            {/* Close Button */}
            <TouchableOpacity
              style={styles.closeButton}
              onPress={() => setIsMenuVisible(false)}
            >
              <Text style={styles.closeButtonText}>Cerrar Menú</Text>
            </TouchableOpacity>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );

};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  logo: {
    width: screenWidth * 0.8,
    height: screenHeight * 0.1,
    marginTop: screenHeight * 0.05,
  },
  postInputContainer: {
    width: '100%',
    paddingHorizontal: '5%',
    alignItems: 'center',
    marginTop: 20,
  },
  button: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#0073e6',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  input: {
    borderWidth: 1,
    borderColor: 'lightgrey',
    width: '100%',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  subirImagenButton: {
    backgroundColor: '#4267B2',
    padding: 10,
    borderRadius: 5,
    width: '100%',
  },
  subirImagenButtonText: {
    textAlign: 'center',
    color: '#fff',
    fontWeight: 'bold',
  },
  flatList: {
    width: '100%',
  },
  postCard: {
    backgroundColor: '#f0f0f0',
    borderRadius: 10,
    padding: 15,
    marginVertical: 10,
    width: '90%',
    alignSelf: 'center',
  },
  postTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  postImage: {
    width: '100%',
    height: 500,
    borderRadius: 10,
  },
   modalOverlay: {
    flex: 1,
    justifyContent: 'flex-end', // Positions the menu at the bottom of the screen
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 16,
    borderTopRightRadius: 20,
    borderTopLeftRadius: 20,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: -2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 5,
    // Set the height or maxHeight as needed, depending on your content
  },
  closeButton: {
    marginTop: 10,
    backgroundColor: '#ccc', // Use your app's color scheme
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'center',
  },
  closeButtonText: {
    fontSize: 16,
    color: '#0073e6', // Use your app's color scheme
  },
  // ... add other styles as needed ...
});

export default Students;
