import { View, Text, StyleSheet, TextInput, TouchableOpacity, Button } from 'react-native';
import {launchCamera, launchImageLibrary} from 'react-native-image-picker';
const ReporteDetail = ({ route }) => {
  const { tipo, descripcion } = route.params;
  const [inputText, setInputText] = useState('');
  const [imageUri, setImageUri] = useState(null);
  

  const handleEnviarTexto = () => {
    // Aquí puedes implementar la lógica para enviar el texto
    alert(`Texto enviado: ${inputText}`);
  };

  const handleSubirImagen = () => {
    let options ={
      storageOptions: {
        path:"image"
      }
    }
    launchImageLibrary(options,response => {

      console.log(response);
    }

    );
    
  };


  return (
     <View style={styles.container}>
      <Text style={styles.title}>{tipo}</Text>
      <Text style={styles.descripcion}>{descripcion}</Text>

      <TextInput
        style={styles.input}
        onChangeText={(text) => setInputText(text)}
        value={inputText}
        placeholder="Escribe aquí"
      />

      <Button title="Enviar Texto" onPress={handleEnviarTexto} />

      <TouchableOpacity style={styles.subirImagenButton} onPress={handleSubirImagen}>
        <Text style={styles.subirImagenButtonText}>Subir Imagen</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 20,
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  header: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  body: {
    flex: 2,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  descripcion: {
    fontSize: 18,
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#0073e6',
    paddingVertical: 15,
    paddingHorizontal: 35,
    borderRadius: 25,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
    input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    marginTop: 10,
    padding: 5,
  },
  subirImagenButton: {
    marginTop: 20,
    backgroundColor: '#004AAD',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 10,
  },
  subirImagenButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default ReporteDetail;
