import React, { useState, useCallback } from 'react';
import { View, Text, Modal, TouchableOpacity, StyleSheet, FlatList, Dimensions, Image,TextInput } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import logoImage from '../../assets/logo.png';
import { launchImageLibrary } from 'react-native-image-picker';


const screenWidth = Dimensions.get('window').width;
const screenHeight = Dimensions.get('window').height;

const Students = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const menuOptions = ["Denuncias", "Reporte", "Usuario"];
  const navigation = useNavigation();
  const [titulo, setTitulo] = useState('');
  const [imageUri, setImageUri] = useState(null);

  const PublicacionesScreen = () => {
  const [publicaciones, setPublicaciones] = useState([]);

  useEffect(() => {
    // Cargar las publicaciones desde el archivo de configuración
    const cargarPublicaciones = async () => {
      try {
        const response = await require('./publicaciones.json');
        const data = JSON.parse(response);
        setPublicaciones(data);
      } catch (error) {
        console.error('Error al cargar las publicaciones desde el archivo de configuración:', error);
      }
    };

    cargarPublicaciones();
  }, []);

  return (
    
    <View style={styles.container}>
      <Image
        source={logoImage}
        style={[styles.logo, { width: screenWidth * 0.8, height: screenHeight * 0.2 }]}
        resizeMode="contain" // Agrega resizeMode para ajustar la imagen
      />
      <TouchableOpacity onPress={toggleMenu} style={styles.menuTrigger}>
        <Text style={styles.menuTriggerText}>Menú☰</Text>
      </TouchableOpacity>
      <TextInput
        style={styles.input}
        onChangeText={(text) => setTitulo(text)}
        value={titulo}
        placeholder="Ingresa el título de la publicación"
      />
      <TextInput
        style={styles.input}
        onChangeText={(text) => setTitulo(text)}
        value={titulo}
        placeholder="Ingresa el título de la publicación"
      />

      <TouchableOpacity
        style={styles.subirImagenButton}
        onPress={handleSeleccionarImagen}
      >
        <Text style={styles.subirImagenButtonText}>Seleccionar Imagen</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.subirImagenButton}
        onPress={handleSeleccionarImagen}
      >
        <Text style={styles.subirImagenButtonText}>Seleccionar Imagen</Text>
      </TouchableOpacity>



      <Modal
        animationType="slide" // Cambiamos la animación para que el menú se deslice desde la izquierda
        transparent={true}
        visible={isMenuVisible}
        onRequestClose={() => {
          setIsMenuVisible(false);
        }}
      >
        <View style={styles.sidebar}>
          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('Denuncias')}
          >
            <FontAwesome name="exclamation-triangle" size={30} color="white" />
            <Text style={styles.buttonText}>Denuncias</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('Reporte')}>
            <FontAwesome name="bar-chart" size={30} color="white" />
            <Text style={styles.buttonText}>Reporte</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('Login')}
          >
            <FontAwesome name="sign-out" size={30} color="white" />
            <Text style={styles.buttonText}>Cerrar Sesión</Text>
             <FlatList
      data={publicaciones}
      keyExtractor={(item) => item.id.toString()}
      renderItem={({ item }) => (
        <View>
          <Text>{item.titulo}</Text>
          <Image source={{ uri: item.imagen }} style={{ width: 100, height: 100 }} />
        </View>
      )}
    />
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#fff'
  },
  menuTrigger: {
    position: 'absolute', // Position absolutely within the parent container
    top: 0, // Align to the top of the container
    left: 0, // Align to the left of the container
    padding: 10, // Add some padding to make it easier to tap
  },
  menuTriggerText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  sidebar: {
    flex: 1,
    backgroundColor: '#dcffff',
  },
  menuItem: {
    fontSize: 18,
    padding: 10,
  },
  logo: {
    width: screenWidth,
    height: screenWidth / 4, // Asumiendo que la relación de aspecto del logo es 4:1
    resizeMode: 'contain',
    alignSelf: 'center', // Asegura que el logo se centre si no ocupa todo el ancho
  },
  button: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#0073e6',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  reportButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#34A853',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
    marginLeft: 10,
  },
});
}
export default Students;
