import React, { useState } from 'react';
import { StyleSheet, View, TextInput, TouchableOpacity, Text, Image, Dimensions } from 'react-native';
import { useNavigation, Alert } from '@react-navigation/native'; 
import logoImage from '../images/logo.png';
import logoNodImage from '../../assets/node.png';

const LoginScreen = () => {
  const [CURP, setCURP] = useState('');
  const [password, setPassword] = useState('');
  const [number, setNumber] = useState('');

  const elementos = [
    {
      name: 'Adolfo Cortes',
      number: '4461248094',
      curp: 'COCA030628HMNRRDA1',
      password: 'clavadovolador'
    },
    {
      name: 'Jose Juan',
      number: '4461248094',
      curp: 'COCA030628HMNRRDA1',
      password: 'pepe'
    },
    {
      name: 'Anibal Tirado',
      number: '4461248094',
      curp: 'COCA030628HMNRRDA1',
      password: 'anibal'
    },
    {
      name: 'Diego Galvan',
      number: '4421230358',
      curp: 'GAMD030810HQTLRGA8',
      password: 'diego'
    },
  ];

  const elemento = elementos.find(elemento => 
    elemento.curp === CURP && elemento.password === password
  );

  const navigation = useNavigation();

  const handleLogin = () => {
    if(elemento){
      navigation.navigate('Inicio', { elemento });
    }
    else{
      alert('Usuario no encontrado!');
    }
  };

  const handleRegistration = () => {
    navigation.navigate('Registration');
  };

  // Obtener dimensiones de la pantalla
  const screenWidth = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;

  return (
    <View style={styles.container}>
      {/* Logo and Text Container */}
      <View style={styles.logoWrapper}>
        <Image
          source={logoImage}
          style={[styles.logo, { width: screenWidth * 0.8, height: screenHeight * 0.2 }]}
          resizeMode="contain" // Agrega resizeMode para ajustar la imagen
        />
        <Image
          source={logoNodImage}
          style={[styles.logoNod, { width: screenWidth * 0.4, height: screenHeight * 0.2 }]}
          resizeMode="contain" // Agrega resizeMode para ajustar la imagen
        />
      </View>

      {/* Inputs */}
      <View style={styles.inputWrapper}>
        <TextInput
          style={styles.input}
          placeholder="CURP"
          placeholderTextColor="#8e8e8e"
          onChangeText={setCURP}
          value={CURP}
        />
        <TextInput
          style={styles.input}
          placeholder="Password"
          placeholderTextColor="#8e8e8e"
          secureTextEntry
          onChangeText={setPassword}
          value={password}
        />
        <TextInput
          style={styles.input}
          placeholder="Número de teléfono"
          placeholderTextColor="#8e8e8e"
          onChangeText={setNumber}
          value={number}
        />
      </View>

      {/* Login Button */}
      <TouchableOpacity style={styles.button} onPress={handleLogin}>
        <Text style={styles.buttonText}>Login</Text>
      </TouchableOpacity>

      {/* Registration Button */}
      <TouchableOpacity style={styles.registrationButton} onPress={handleRegistration}>
        <Text style={styles.registrationButtonText}>Registrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#fff',
  },
  logoWrapper: {
    marginBottom: 20,
    alignItems: 'center',
  },
  logo: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  logoNod: {
    borderRadius: 70,
    backgroundColor: '#FFFFFF',
    justifyContent: 'center',
    alignItems: 'center',
  },
  inputWrapper: {
    width: '80%',
  },
  input: {
    height: 50,
    backgroundColor: '#fff',
    marginBottom: 20,
    paddingHorizontal: 15,
    borderRadius: 25,
    borderWidth: 1,
    borderColor: '#0833a2',
  },
  button: {
    backgroundColor: '#0073e6',
    paddingVertical: 15,
    paddingHorizontal: 35,
    borderRadius: 25,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  registrationButton: {
    marginTop: 10,
    backgroundColor: '#33cc33',
    paddingVertical: 15,
    paddingHorizontal: 35,
    borderRadius: 25,
  },
  registrationButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default LoginScreen;
