import React, { useState, useRef, useEffect } from 'react';
import { View, Text, Animated, TouchableOpacity, StyleSheet, FlatList, Dimensions, Image, TextInput } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import logoImage from '../../assets/logo.png';
import { launchImageLibrary } from 'react-native-image-picker';

const screenWidth = Dimensions.get('window').width;
const screenHeight = Dimensions.get('window').height;

const Students = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const [titulo, setTitulo] = useState('');
  const [imageUri, setImageUri] = useState(null);
  const navigation = useNavigation();
  const slideAnim = useRef(new Animated.Value(-screenWidth)).current; // Initial position of the menu

  const publicacionesData = [
    {
      id: 1,
      titulo: "HAY UN BACHE EN BERNARDO QUINTANA CON CONSTITUYENTES",
      imagen: require('../../assets/bache.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
    {
      id: 2,
      titulo: "Publicación 2",
      imagen: require('../../assets/reparacion.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
    {
      id: 3,
      titulo: "QUE GUAPO MI AMOR",
      imagen: require('../../assets/fofo.jpeg'), // Usa `require` para cargar imágenes desde recursos locales
    },
  ];
  const [publicaciones, setPublicaciones] = useState(publicacionesData);

  useEffect(() => {
    // Trigger the slide animation whenever isMenuVisible changes
    Animated.timing(slideAnim, {
      toValue: isMenuVisible ? 0 : -screenWidth,
      duration: 300,
      useNativeDriver: true,
    }).start();
  }, [isMenuVisible, slideAnim]);

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  

  const selectOption = (option) => {
    console.log(`Opción seleccionada: ${option}`);
    setIsMenuVisible(false);
  };

  const handleSeleccionarImagen = () => {
    // Configuración para la selección de imágenes
    const options = {
      mediaType: 'photo', // Solo permitir seleccionar fotos
      quality: 0.5, // Calidad de la imagen (0.0 - 1.0)
    };

    // Abrir el cuadro de diálogo para seleccionar una imagen
    launchImageLibrary(options, (response) => {
      if (response.didCancel) {
        // El usuario canceló la selección de imagen
      } else if (response.errorMessage) {
        // Ocurrió un error al seleccionar la imagen
        console.error('Error al seleccionar la imagen: ', response.errorMessage);
      } else {
        // Imagen seleccionada con éxito
        const { uri } = response;

        // Actualizar el estado 'imageUri' con la ruta de la imagen seleccionada
        setImageUri(uri);
      }
    });
  };
  return (
    <View style={styles.container}>
      {/* Menu Button */}
      <TouchableOpacity onPress={toggleMenu} style={styles.menuButton}>
        <Text style={styles.menuButtonText}>Menu ☰</Text>
      </TouchableOpacity>

      {/* Main Content */}
      <Image
        source={logoImage}
        style={styles.logo}
        resizeMode="contain"
      />
      <View style={styles.postInputContainer}>
        <TextInput
          style={styles.input}
          onChangeText={setTitulo}
          value={titulo}
          placeholder="Ingresa el título de la publicación"
        />
        <TouchableOpacity
          style={styles.subirImagenButton}
          onPress={() => {}} // You should implement this function
        >
          <Text style={styles.subirImagenButtonText}>Seleccionar Imagen</Text>
        </TouchableOpacity>
      </View>

      <FlatList
        data={publicaciones}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.postCard}>
            <Text style={styles.postTitle}>{item.titulo}</Text>
            <Image source={item.imagen} style={styles.postImage} />
          </View>
        )}
        style={styles.flatList}
      />

      {/* Overlay to close menu */}
      {isMenuVisible && (
        <TouchableOpacity style={styles.overlay} onPress={toggleMenu} activeOpacity={1} />
      )}

      {/* Animated Sidebar */}
      <Animated.View style={[styles.sidebar, { transform: [{ translateX: slideAnim }] }]}>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate('Denuncias')}
        >
          <FontAwesome name="exclamation-triangle" size={30} color="white" />
          <Text style={styles.buttonText}>Denuncias</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate('Reporte')}
        >
          <FontAwesome name="bar-chart" size={30} color="white" />
          <Text style={styles.buttonText}>Reporte</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate('Login')}
        >
          <FontAwesome name="sign-out" size={30} color="white" />
          <Text style={styles.buttonText}>Cerrar Sesión</Text>
        </TouchableOpacity>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  menuButton: {
    position: 'absolute',
    top: 40,
    left: 10,
    zIndex: 2,
    padding: 10,
  },
  menuButtonText: {
    fontSize: 24,
    color: '#4267B2',
  },
  logo: {
    width: screenWidth * 0.8,
    height: screenHeight * 0.1,
    marginTop: screenHeight * 0.05,
  },
  postInputContainer: {
    width: '100%',
    paddingHorizontal: '5%',
    alignItems: 'center',
    marginTop: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: 'lightgrey',
    width: '100%',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  subirImagenButton: {
    backgroundColor: '#4267B2',
    padding: 10,
    borderRadius: 5,
    width: '100%',
  },
  subirImagenButtonText: {
    textAlign: 'center',
    color: '#fff',
    fontWeight: 'bold',
  },
  flatList: {
    width: '100%',
  },
  postCard: {
    backgroundColor: '#f0f0f0',
    borderRadius: 10,
    padding: 15,
    marginVertical: 10,
    width: '90%',
    alignSelf: 'center',
  },
  postImage: {
    width: '100%',
    height: 500,
    borderRadius: 10,
  },
  sidebar: {
    position: 'absolute',
    left: 0,
    top: 0,
    bottom: 0,
    width: screenWidth * 0.8,
    backgroundColor: '#fff',
    padding: 20,
    zIndex: 1,
  },
  button: {
    // Style for the buttons in the sidebar
  },
  buttonText: {
    // Style for the text in the sidebar buttons
  },
  overlay: {
    position: 'absolute',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1,
  },
  // ... other styles ...
});

export default Students;
