import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput } from 'react-native';

const DenunciaDetailScreen = ({ route }) => {
  const { tipo, descripcion } = route.params;
  const [inputText, setInputText] = useState('');
  const [showInput, setShowInput] = useState(false); // Agregar estado para mostrar u ocultar el cuadro de entrada

  const toggleInput = () => {
    setShowInput(!showInput);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{tipo}</Text>
      <Text style={styles.descripcion}>{descripcion}</Text>
      {showInput && ( // Mostrar el cuadro de entrada si showInput es verdadero
        <TextInput
          style={styles.input}
          onChangeText={(text) => setInputText(text)}
          value={inputText}
          placeholder="Escribe aquÃ­"
        />
      )}
      <TouchableOpacity onPress={toggleInput}>
        <Text>{showInput ? "Ocultar Cuadro de Texto" : "Mostrar Cuadro de Texto"}</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  descripcion: {
    fontSize: 18,
  },
  input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    marginTop: 10,
    padding: 5,
  },
});

export default DenunciaDetailScreen;
